        val worksheet = workbook.activeSheet

        // change table style to TableStyleMedium14
        val totalsTable = worksheet.tables.get("TotalsTable")
        totalsTable.tableStyle = workbook.tableStyles.get("TableStyleMedium14")

        // change chart type to column stacked
        val chart = worksheet.shapes.get(0).chart
        chart.chartType = ChartType.ColumnStacked
        chart.columnGroups.get(0).overlap = 100

        // set a bigger font size for chart title
        chart.chartTitle.font.size = 24.0
        chart.chartTitle.font.bold = true

        // give a one color gradient to chart area
        chart.chartArea.format.fill.oneColorGradient(GradientStyle.Horizontal, 1, 0.0)
        chart.chartArea.format.fill.gradientStops.get(0).color.objectThemeColor = ThemeColor.Accent6
        chart.chartArea.format.fill.gradientStops.get(0).color.brightness = 0.8

        // make fill of plot area transparant
        chart.plotArea.format.fill.transparency = 1.0

        // config series1 of chart
        val series1 = chart.seriesCollection.get(0)
        series1.hasDataLabels = true
        series1.format.fill.color.objectThemeColor = ThemeColor.Accent1

        // give some formatting for the first point of series1
        val point1 = series1.points.get(0)
        point1.format.line.color.rgb = Color.GetBlack()
        point1.format.line.weight = 2.0
        point1.format.line.dashStyle = LineDashStyle.Dash

        // config series2 of chart
        val series2 = chart.seriesCollection.get(1)
        series2.hasDataLabels = true
        series2.format.fill.color.objectThemeColor = ThemeColor.Accent6
        series2.dataLabels.font.color.rgb = Color.GetRed()

        // get the value axis
        val value_axis = chart.axes.item(AxisType.Value, AxisGroup.Primary)

        // show the display unit as thousands for value axis
        value_axis.hasDisplayUnitLabel = true
        value_axis.displayUnit = DisplayUnit.Thousands

        // give a color for the major grid line of value axis
        value_axis.majorGridlines.format.line.color.objectThemeColor = ThemeColor.Accent6

